if (globalVars("RoleReactionMenuAlreadyRan") == null) {
    const Discord = require("discord.js");
    Actions.storeValue(true, 3, "RoleReactionMenuAlreadyRan", cache);
    const fs = require('fs');
    const request = require('request');

    //Settings
    const dmUserAfter = true; // Default: true, can be changed to false
    var messageSentAdd = 'Successfully added the role {rolename} in {server}';
    var messageSentRemove = 'Successfully removed the role {rolename} in {server}';
    var debug = false; // Default: false, can be changed to true
    var checkUpdates = true;
    // The only variables that work rn, {rolename} and {server}

    var requiredIntents = ["GUILDS","GUILD_MEMBERS","GUILD_MESSAGE_REACTIONS","GUILD_MESSAGES"];
        var intentCheck = new Discord.Intents(DBM.Bot.intents()).toArray();
        requiredIntents.forEach(intent => {
            if (!intentCheck.includes(intent)) {
                console.log(`[Role Reaction] You don't have the ${intent} intent enabled!`)
            }
        })



    if (checkUpdates) {
        request('https://raw.githubusercontent.com/ItzPike/DBM-Rawdata-Releases/master/Role%20Reaction%20Menu/versionCheck.json', function(error, response, body) {
            if (response.statusCode == 200) {
                var allVersions = JSON.parse(body);

                var commandVersions = {};
                var eventVersions = {};
                var commandKeys = [];
                var eventKeys = [];

                allVersions["commands"].forEach(commandVer => {
                    commandVersions[commandVer[0]] = commandVer[1];
                    commandKeys.push(commandVer[0]);
                });

                allVersions["events"].forEach(eventVer => {
                    eventVersions[eventVer[0]] = eventVer[1];
                    eventKeys.push(eventVer[0])
                })

                fs.readFile("./data/commands.json", async function(err, data) {
                    var data = JSON.parse(data);

                    var systemCommands = await data.filter(command => command != null && commandKeys.includes(command["_id"]));

                    for (i = 0; i < systemCommands.length; i++) {
                        var systemActionFind = await systemCommands[i]["actions"].filter(actionInfo => actionInfo.name = "Run Script" && actionInfo.code != null);

                        if (systemActionFind.length != 0) {
                            var versionCheckSplit = systemActionFind[0].code.split("VersionCheck:");
                            if (versionCheckSplit[1] != null && versionCheckSplit[1] != commandVersions[systemCommands[i]["_id"]] && !isNaN(versionCheckSplit[versionCheckSplit.length - 1])) {
                                console.log(`[Role Reaction] The command ${systemCommands[i]["name"]} needs an update!`)
                            }
                        }
                    }
                })

                fs.readFile("./data/events.json", async function(err, data) {
                    var data = JSON.parse(data);

                    var systemEvent = await data.filter(event => event != null && eventKeys.includes(event["_id"]));

                    for (i = 0; i < systemEvent.length; i++) {
                        var systemEventFind = await systemEvent[i]["actions"].filter(actionInfo => actionInfo.name = "Run Script" && actionInfo.code != null);

                        if (systemEventFind.length != 0) {
                            var versionCheckSplit = systemEventFind[0].code.split("VersionCheck:");
                            if (versionCheckSplit != null && versionCheckSplit[versionCheckSplit.length - 1] != eventVersions[systemEvent[i]["_id"]] && !isNaN(versionCheckSplit[versionCheckSplit.length - 1])) {
                                console.log(`[Role Reaction] The event ${systemEvent[i]["name"]} needs an update!`)
                            }
                        }
                    }
                })
            }
        });
    }

    if (globalVars("reactionRoleMoved") == null) {
        Files.saveGlobalVariable("reactionRoleMoved", true);

        if (!fs.existsSync(`./data/roleReaciton`)) {
            fs.mkdirSync(`./data/roleReaciton`);
        }

        ["reactionRoleData"].forEach(fileName => {
            if (fs.existsSync(`./data/${fileName}.json`)) {
                fs.renameSync( `./data/${fileName}.json`, `./data/roleReaciton/${fileName}.json`);
            }
        });
    }

    if (fs.existsSync('./data/roleReaciton/reactionRoleData.json')) {
        fs.readFile('./data/roleReaciton/reactionRoleData.json', function read(err, data) {
            if (err) {
                throw err;
            }
            data = JSON.parse(data)

            Actions.storeValue(data, 3, 'reactionRoleInf', cache);
        });
    } else {
        fs.writeFile("./data/roleReaciton/reactionRoleData.json", "{}", function(err) {
            if (err) {
                return console.log(err);
            }
        });
        Actions.storeValue({}, 3, 'reactionRoleInf', cache);
    }

    client.on('raw', packet => {
        if (!['MESSAGE_REACTION_ADD', 'MESSAGE_REACTION_REMOVE'].includes(packet.t)) return;
        const channel = client.channels.cache.get(packet.d.channel_id);
        if (channel.messages.cache.has(packet.d.message_id)) return;
        channel.messages.fetch(packet.d.message_id).then(message => {
            const emoji = packet.d.emoji.id ? `${packet.d.emoji.name}:${packet.d.emoji.id}` : packet.d.emoji.name;
            const reaction = message.reactions.cache.get(emoji);
            const reactionUser = client.users.cache.get(packet.d.user_id);
            if (reaction) reaction.users.cache.set(packet.d.user_id, reactionUser);
            if (reactionUser == null) {
                console.log("Role reaction error: User is not defined! Please make sure you have followed the new setup for intents to allow this command to work!");
            } else {
                if (!reactionUser.bot) {
                    if (packet.t === 'MESSAGE_REACTION_ADD') {
                        client.emit('messageReactionAdd', reaction, reactionUser);
                    }
                    if (packet.t === 'MESSAGE_REACTION_REMOVE') {
                        client.emit('messageReactionRemove', reaction, reactionUser);
                    }
                }
            }
        });
    });

    client.on('messageReactionAdd', async function(reaction, user) {
        user = await user;

        if (user == null) {
            console.log("Role reaction error: User is not defined! Please make sure you have followed the new setup for intents to allow this command to work!");
        } else {
            if (!user.bot) {
                const reactionData = globalVars("reactionRoleInf");
                const reactionGuild = reaction.message.guild;
                const reactionMessage = reaction.message;
                if (reactionData.hasOwnProperty(reactionMessage.id)) {
                    var emojiName = reaction.emoji.name;
                    var roleInData = await reactionData[reactionMessage.id].filter(re => re[0] == emojiName || re[0].name == emojiName)
                    if (roleInData.length != 0) {
                        var guilduser = await reactionGuild.members.cache.get(user.id);
                        var roleData = roleInData[0][1];

                        if (!guilduser.roles.cache.has(roleData)) {
                            var reactionRole = await reactionGuild.roles.cache.get(roleData);

                            guilduser.roles.add(reactionRole)
                                .catch(function(error) {
                                    guilduser.send("Sorry! I couldn't add the role due to either permission issues or the role's position may be higher than my highest role's position. Please contact an adminstrator about this issue.")
                                        .catch(function(error) {
                                            if (error != "DiscordAPIError: Cannot send messages to this user" && debug) {
                                                console.log(error);
                                            } else if (debug) {
                                                console.log(`Unable to send user a message || ${guilduser.name}`)
                                            }
                                        });
                                    if (debug) {
                                        console.log(error);
                                    }
                                });


                            if (dmUserAfter) {
                                guilduser.send(messageSentAdd.replace("{rolename}", `**${reactionRole.name}**`).replace("{server}", `**${reactionGuild.name}**`))
                                    .catch(function(error) {
                                        if (error != "DiscordAPIError: Cannot send messages to this user" && debug) {
                                            console.log(error);
                                        } else if (debug) {
                                            console.log(`Unable to send user a message || ${guilduser.name}`)
                                        }
                                    });
                            }

                        }
                    }
                }
            }
        }

    });

    client.on('messageReactionRemove', async function(reaction, user) {
        user = await user;

        if (user == null) {
            console.log("Role reaction error: User is not defined! Please make sure you have followed the new setup for intents to allow this command to work!");
        } else {
            if (!user.bot) {
                const reactionData = globalVars("reactionRoleInf");
                const reactionGuild = reaction.message.guild
                const reactionMessage = reaction.message;
                if (reactionData.hasOwnProperty(reactionMessage.id)) {
                    var emojiName = reaction.emoji.name;
                    var roleInData = await reactionData[reactionMessage.id].filter(re => re[0] == emojiName || re[0].name == emojiName)
                    if (roleInData.length != 0) {
                        var guilduser = await reactionGuild.members.cache.get(user.id);
                        var roleData = roleInData[0][1];

                        if (guilduser.roles.cache.has(roleData)) {
                            var reactionRole;
                            reactionRole = await reactionGuild.roles.cache.get(roleData);

                            guilduser.roles.remove(reactionRole)
                                .catch(function(error) {
                                    guilduser.send("Sorry! I couldn't remove the role due to either permission issues or the role's position may be higher than my highest role's position. Please contact an adminstrator about this issue.")
                                        .catch(function(error) {
                                            if (error != "DiscordAPIError: Cannot send messages to this user" && debug) {
                                                console.log(error);
                                            } else if (debug) {
                                                console.log(`Unable to send user a message || ${guilduser.name}`)
                                            }
                                        });
                                    if (debug) {
                                        console.log(error);
                                    }
                                });

                            if (dmUserAfter) {
                                guilduser.send(messageSentRemove.replace("{rolename}", `**${reactionRole.name}**`).replace("{server}", `**${reactionGuild.name}**`))
                                    .catch(function(error) {
                                        if (error != "DiscordAPIError: Cannot send messages to this user" && debug) {
                                            console.log(error);
                                        } else if (debug) {
                                            console.log(`Unable to send user a message || ${guilduser.name}`)
                                        }
                                    });
                            }
                        }
                    }
                }
            }
        }
    });
}

// VersionCheck:4
